@Component({
  selector: 'app-root',
  template: `
    <hello></hello>
    <button (click)="onClick()">Trigger change detection</button>
  `
})
export class AppComponent {
  constructor() {
    setInterval(() => console.log('Main interval event'), 2000);
  }

  onClick() {
    console.log('on click');
  }
}




