import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <hello></hello>
    <button (click)="onClick()">Trigger change detection</button>
    <config></hello>
    <button (click)="onClick()">Trigger change detection</button>
    <users></users>
  `
})
export class AppComponent {
  onClick() {
    console.log('on click');
  }
}
