import { Component, Input } from '@angular/core';

@Component({
  selector: 'users',
  template: `
    <user *ngFor="let item of users" [user]="user"></user>
  `
})
export class UsersComponent {
  private users = [ 'Admin', 'Anon', 'User' ];

  get runChangeDetection() {
    console.log('Checking the view');
    return true;
  }
}
