import { Component, Input } from '@angular/core';

@Component({
  selector: 'config',
  template: `
    <h1>Url {{config.url}}!</h1>
    {{runChangeDetection}}
  `
})
export class HelloComponent {
  @Input() config = { url: '' };
  get runChangeDetection() {
    console.log('Checking the view config');
    return true;
  }
}
